import React from 'react';
import logo from '../img/logo.svg'; // Imagem padrão do React
import '../styles/postList.css'; // Estilos para a lista de postagens

const PostList = ({ posts, onEdit, onDelete }) => {
  if (posts.length === 0) {
    return <p>Nenhuma postagem disponível.</p>;
  }

  return (
    <div>
      {posts.map((post) => (
        <div key={post.id} className="post-item">
          <img
            src={post.imageUrl || logo}
            alt="Post thumbnail"
            className="post-image"
          />
          <div className="post-content">
            <h2>{post.title}</h2>
            <p>{post.content.length > 100 ? `${post.content.substring(0, 100)}...` : post.content}</p>
            <button onClick={() => onEdit(post)}>Editar</button>
            <button onClick={() => onDelete(post.id)}>Excluir</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PostList;
