import React, { useState } from 'react';
import PostForm from './components/PostForm';
import PostList from './components/PostList';

function App() {
  const [posts, setPosts] = useState([]); // Estado para armazenar as postagens
  const [showForm, setShowForm] = useState(false); // Controla a exibição do formulário

  // Função para adicionar uma nova postagem à lista
  const handleAddPost = (newPost) => {
    setPosts([newPost, ...posts]); // Adiciona a nova postagem ao topo da lista
  };

  return (
    <div>
      <button onClick={() => setShowForm(!showForm)}>
        {showForm ? 'Fechar Formulário' : 'Adicionar Postagem'}
      </button>

      {showForm && <PostForm onAddPost={handleAddPost} />}

      <PostList posts={posts} />
    </div>
  );
}

export default App;
