import React, { useState } from 'react';

import PostForm from './components/PostForm';
import PostList from './components/PostList';
import Modal from './components/Modal';

import './styles/app.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingPost, setEditingPost] = useState(null); // Estado para editar post

  // Função para adicionar ou atualizar uma postagem
  const handleAddOrUpdatePost = (newPost) => {
    if (editingPost) {
      setPosts(
        posts.map((post) => (post.id === editingPost.id ? newPost : post))
      );
      setEditingPost(null); // Reseta o post em edição
    }
    else {
      setPosts([newPost, ...posts]);
    }
    setShowForm(false);
  };

  // Função para editar uma postagem
  const handleEditPost = (post) => {
    setEditingPost(post);
    setShowForm(true); // Abre o modal com os dados do post
  };

  // Função para deletar uma postagem
  const handleDeletePost = (id) => {
    setPosts(posts.filter((post) => post.id !== id));
  };

  return (
    <div className="App">
      <aside className="sidebar">
        <h2>Minhas Redes</h2>
        <ul>
          <li>
            <a href="https://www.linkedin.com/in/joaoasouzan/" target="_blank" rel="noopener noreferrer">
              <img src="../public/img/LI-In-Bug.png" alt="LinkedIn" />
              joaoasouzan
            </a>
          </li>
          <li>
            <a href="https://github.com/JoaoASouzaN" target="_blank" rel="noopener noreferrer">
              <img src="../public/img/gitHub-mark.svg" alt="GitHub" />
              JoaoASouzaN
            </a>
          </li>
          <li>
            <a href="mailto:joaoif.eletro@gmail.com">
              <img src="../public/img/email.svg" alt="Email" />
              joaoif.eletro@gmail.com
            </a>
          </li>
        </ul>
      </aside>

      <main className="content">
        <button onClick={() => setShowForm(true)} className="add-post-btn">Adicionar Postagem</button>

        <Modal isOpen={showForm} onClose={() => setShowForm(false)}>
          <PostForm
            onAddPost={handleAddOrUpdatePost}
            editingPost={editingPost}
          />
        </Modal>

        <PostList posts={posts} onEdit={handleEditPost} onDelete={handleDeletePost} />
      </main>
    </div>
  );
}

export default App;
