import React, { useState } from 'react';
import logo from '../img/logo.svg'; // Imagem padrão do React
import '../styles/postList.css'; // Estilos para a lista de postagens

const PostList = ({ posts, onEdit, onDelete }) => {
  const [expandedPostId, setExpandedPostId] = useState(null);

  const handleReadMore = (postId) => {
    setExpandedPostId(postId);
  };

  const handleReadLess = () => {
    setExpandedPostId(null);
  };

  if (posts.length === 0) {
    return <p>Nenhuma postagem disponível.</p>;
  }

  return (
    <div>
      {posts.map((post) => (
        <div key={post.id} className="post-item">
          <img
            src={post.imageUrl || logo}
            alt="Post thumbnail"
            className="post-image"
          />
          <div className="post-content">
            <h2>{post.title}</h2>
            <p className={expandedPostId === post.id ? 'full-content' : 'collapsed-content'}>
              {post.content}
            </p>
            {post.content.length > 200 && (
              <button onClick={() => expandedPostId === post.id ? handleReadLess() : handleReadMore(post.id)}>
                {expandedPostId === post.id ? 'Leia menos' : 'Leia mais'}
              </button>
            )}
            <button onClick={() => onEdit(post)}>Editar</button>
            <button onClick={() => onDelete(post.id)}>Excluir</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PostList;
