import React from 'react';
import logo from './img/logo.svg';

const DEFAULT_IMAGE = 'link-para-imagem-padrao.jpg'; // Defina a URL de uma imagem padrão

const PostList = ({ posts, onDelete }) => {
  return (
    <div>
      {posts.length === 0 ? (
        <p>Nenhuma postagem disponível.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} style={{ display: 'flex', marginBottom: '20px' }}>
            <img
              src={post.imageUrl || logo}
              alt="Post thumbnail"
              style={{ width: '40%', height: 'auto' }}
            />
            <div style={{ width: '60%', paddingLeft: '10px' }}>
              <h2>{post.title}</h2>
              <p>
                {post.content.length > 100
                  ? `${post.content.substring(0, 100)}...`
                  : post.content}
              </p>
              {post.content.length > 100 && <button>Leia mais</button>}
              <button onClick={() => onDelete(post.id)}>Deletar</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default PostList;
