import React, { useState } from 'react';
import logo from '../img/logo.svg'; // Imagem padrão do React
import '../styles/postList.css'; // Estilos para a lista de postagens

const PostList = ({ posts, onEdit, onDelete }) => {
  const [selectedPost, setSelectedPost] = useState(null);

  const handleReadMore = (post) => {
    setSelectedPost(post);
  };

  const handleCloseModal = () => {
    setSelectedPost(null);
  };

  if (posts.length === 0) {
    return <p>Nenhuma postagem disponível.</p>;
  }

  return (
    <div>
      {posts.map((post) => (
        <div key={post.id} className="post-item">
          <img
            src={post.imageUrl || logo}
            alt="Post thumbnail"
            className="post-image"
          />
          <div className="post-content">
            <h2>{post.title}</h2>
            <p className={post.content.length > 200 ? 'collapsed-content' : 'full-content'}>
              {post.content}
            </p>
            {post.content.length > 200 && (
              <button onClick={() => handleReadMore(post)}>
                Leia mais
              </button>
            )}
            <button onClick={() => onEdit(post)}>Editar</button>
            <button onClick={() => onDelete(post.id)}>Excluir</button>
          </div>
        </div>
      ))}
      
      {/* Modal para visualização completa */}
      {selectedPost && (
        <div className="modal" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2>{selectedPost.title}</h2>
            <p>{selectedPost.content}</p>
            <button className="modal-close" onClick={handleCloseModal}>
              Fechar
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PostList;
